            /**
             * Grid Columns
             * @type {number}
             */
            window.GridColumns = 5;

            // Collapse Styles
            $('#ef-styles')
                .on('click', '#ef-styles-collapse-all', function (e) {
                    e.preventDefault();
                    $('body').find('.collapse').removeClass('show');
                    const bsCollapse = new bootstrap.Collapse('.accordion-collapse', {
                        toggle: false
                    });
                    bsCollapse.hide();
                })
                .on('click', '#ef-styles-expand-all', function (e) {
                    e.preventDefault();
                    $('body').find('.collapse').addClass('show');
                    const bsCollapse = new bootstrap.Collapse('.accordion-collapse', {
                        toggle: false
                    });
                    bsCollapse.show();
                });

            // Default values
            window.polyglot = new Polyglot(JSON.parse(i18n));
            initForm = JSON.parse(initForm);
            components = JSON.parse(components);
            cssRules = JSON.parse(cssRules);
            _.each(cssRules, function (cssRule) {
                cssRule['properties'] = JSON.parse(cssProperties);
            });
            window.defaultCssRules = JSON.stringify(cssRules);

            // CSRF configuration
            this.csrfParam = $('meta[name="csrf-param"]').attr('content');
            this.csrfToken = $('meta[name="csrf-token"]').attr('content');

